require 'acceptance_helper'

resource 'customers/update', document: :v1 do
  include_context :content_headers
  header 'Authorization', :token

  parameter :id, 'Customer id', required: true
  parameter :type, 'customers', scope: :data, required: true
  with_options scope: [:data, :attributes], required: true do |o|
    o.parameter :name, 'Customer name'
    o.parameter :surname, 'Customer surname'
    o.parameter :phone, 'Customer phone number'
    o.parameter :avatar, 'Base64 encoded avatar: https://developer.mozilla.org/ru/docs/Web/API/FileReader/readAsDataURL'
  end

  let(:token) { "Token token=#{customer.http_token.key}" }

  let(:id) { customer.id }
  let(:type) { 'customers' }
  let(:name) { 'New name' }
  let(:surname) { 'New name' }
  let(:phone) { '+1234567890' }

  # let(:raw_post) {
  #   attributes =
  #     params['data']['attributes'].
  #     merge('addresses_attributes' => [params['data']['attributes']['addresses_attributes'].merge(id: customer.addresses.first.id)])
  #     params = { 'data' => {'type' => 'customers', 'attributes' => attributes} }
  #   params.to_json
  # }

  patch "/customers/:id" do

    context 'Successfully update customer profile without avatar' do
      let(:customer) { create(:signed_in_customer, addresses: [create(:address)]) }
      let(:avatar) { "data:image/png;base64," }

      example_request 'Successfully update customer profile without avatar' do
        explanation 'Avatar field is filled in according to https://developer.mozilla.org/ru/docs/Web/API/FileReader/readAsDataURL'
        ap json(response_body)
        expect(status).to eq(200)
        expect(response_headers['Content-Type']).to match("#{Mime[:json].to_s}; charset=utf-8")
        expect(json(response_body)[:data][:attributes][:avatar]).to be_present
        # expect(File.exist?(customer.avatar.file&.path)).to be_truthy
      end
    end

    context 'Successfully update customer profile with avatar' do
      let(:customer) { create(:signed_in_customer, addresses: [create(:address)], avatar: "data:image/png;base64,") }

      example_request 'Successfully update customer profile wit avatar' do
        explanation 'Avatar field is filled in according to https://developer.mozilla.org/ru/docs/Web/API/FileReader/readAsDataURL'
        ap json(response_body)
        expect(status).to eq(200)
        expect(response_headers['Content-Type']).to match("#{Mime[:json].to_s}; charset=utf-8")
        expect(json(response_body)[:data][:attributes][:avatar]).to be_present
        # expect(File.exist?(customer.avatar.file&.path)).to be_truthy
      end
    end

  end
end
